//获取用户信息
export const WALLET_URL = '/v1/global/getCurrencyAndNetwork';
interface CryptoNetwork {
  id: number;
  icon: string;
  name: string;
  logo: string;
}

export interface CryptoCurrency {
  id: number;
  brief: string;
  icon: string;
  name: string;
  logo: string;
  usdtRate: number; // Assuming usdtRate is a number
}

export interface WalletResp {
  cryptoNetworks: CryptoNetwork[];
  cryptoCurrencies: CryptoCurrency[];
}

export const TRANSITION_RUL = '/v1/global/getCryptoTransition';
export interface TransitionRespItem {
  currencyId: number;
  networkId: number;
  pubKey: string;
  minCharge: number;
  maxDraw: number;
}

//提现
export const WITHDRAW_URL = '/v1/balance/payout';
export interface WithdrawReq {
  currencyId: number;
  networkId: number;
  payoutAddress: string;
  amount: number;
}
export interface WithdrawResp {
  data: string;
}

export const GET_PAYOUTRECORD = '/v1/balance/getPayoutRecord';
export interface PayoutRecordReq {
  pageSize: number;
  pageNum: number;
}
export interface PayoutRecordItem {
  id: number;
  currecnyId: number;
  networkId: number;
  payoutAddress: string;
  amount: number;
  memo: null;
  transferOutAddress: null;
  userId: number;
  status: number;
  time: string;
}
export interface GetPayoutRecordResp {
  pages: number;
  total: number;
  pageNum: number;
  data: PayoutRecordItem[];
}

export const WALLETADDRESS = '/v1/balance/getWalletAddress';
export interface WalletAdderssReq {
  currencyId: string;
  networkId?: string;
}

/**创建充值 */
export const CREATEORDER = '/v1/thirdPay/createOrder';
export interface CreateOrederReq {
  userId: number;
  amount: number;
  payType: string;
}
export const CREATEPAYOUT = '/v1/thirdPay/createPayout';
export interface PayoutReq {
  amount: number;
  type: string;
  userId: number;
  accountNo: string;
}
export interface PayoutResp {
  msg: string;
  type: number;
}
/**获取充值配置地址 */
export const RECHARGECONFIG = '/v1/global/getRechargeConfig';
export interface RechargeConfigResp {
  minValue: number;
  maxValue: number;
  reward: string;
  multiple: number;
}
export interface RewardItem {
  initialValue: number;
  obterValue: number;
  index?: number;
}
/**修改 RechargeConfigResp 接口，包括处理后的奖励数据 */
export interface ProcessedRechargeConfigResp {
  minValue: number;
  maxValue: number;
  reward: RewardItem[]; // 处理后的格式
  multiple: number;
  selected?: number;
}

/**查询可以提现的余额地址 */
export const PAYOUTAMOUNT = '/pay/v1/queryPayoutAmount';
export interface PayoutAmountReq {
  userId: number;
}

/**创建kakapay订单 */
export const CREATE_ORDER = '/v1/kaKaPay/createOrder';
export interface CreateOrderReq {
  amount: number;
}
export interface CreateOrderResp {
  qrcode: string;
  qrcodeImage: string;
}

/**创建kakapay提现 */
export const KAKA_PAYOUT = '/v1/kaKaPay/payout';
export interface KakaPayoutReq {
  accountType: string;
  accountNumber: string;
  amount: number;
}

/**充值 */
export const PAY_RECHARGE = '/pay/v1/recharge';
export interface PayRechargeReq {
  amount: number;
  payType: string;
}
export interface PayRechargeResp {
  type: string;
  url: string;
  qrcode: string;
  qrcodeImage: string;
}
/**提现 */
export const PAY_PAYOUT = '/pay/v1/payout';
export interface PayPayoutReq {
  amount: number;
  type: string;
  accountNo: string;
}
