import { reactive } from 'vue';
import { wheel } from '@/games/wheel';
import { cashback } from '../cashback';
import { redEnvelopeRain } from '../redEnvelopeRain/game';
import dataCenter from '@/dataCenter';
import { getRouter, openModal } from '@/manager/view';
import WheelPop from '@/games/wheel/wheelPop.vue';
import Receba from '../home/other/Receba.vue';
import { RightSideBarState } from '@/dataCenter/layout';
import reentryRewardVue from '../reentryReward/index.vue';
import { lang } from '@/lang';
export interface TreasureBoxRewardItem {
  key: number;
  background: {
    background: string;
  };
  icon: string;
  title: string;
  text: string;
  tips?: string;
  tipsIcon?: string;
  btn?: {
    btnText: string;
    active: boolean;
  };
  amount?: string;
  text2?: string;
}

export interface TreasureBoxRewardCategory {
  key: number;
  title: string;
  content: TreasureBoxRewardItem[];
}
class BonusCabinet {
  public showMenu = false;
  public openDownMenu = false;
  public treasureBoxReward = reactive([
    {
      key: 1,
      get title() {
        return lang.v.tb_tb_jiangli;
      },
      content: [
        {
          key: 1.1,
          background: {
            background:
              'radial-gradient( 97.13% 60.1% at 0% 0%, rgba(0,70,253,0.35) 0%, #121923 100%), rgb(17, 25, 35)',
          },
          icon: require('@/assets/treasurebox/1.png'),
          get title() {
            return lang.v.tb_desc;
          },
          get text() {
            return lang.v.tb_xuan;
          },
          get tips() {
            return lang.v.tb_dajiang;
          },
          tipsIcon: require('@/assets/treasurebox/666.png'),
          get btn() {
            return {
              btnText: wheel.active.value ? lang.v.tb_wheel : wheel.time.value,
              active: wheel.active.value,
            };
          },
        },
        {
          key: 1.2,
          background: {
            background:
              'radial-gradient( 97.13% 60.1% at 0% 0%, rgba(235,77,55,0.35) 0%, #121923 100%), rgb(17, 25, 35)',
          },
          icon: require('@/assets/treasurebox/2.png'),
          get title() {
            return lang.v.tb_jiu;
          },
          get text() {
            return lang.v.tb_jiu_san;
          },
          get tips() {
            return lang.v.tb_jiu_amount;
          },
          tipsIcon: require('@/assets/treasurebox/666.png'),
          get btn() {
            return {
              btnText: lang.v.tb_yaoqing,
              active: true,
            };
          },
        },
        {
          key: 1.3,
          background: {
            background:
              'radial-gradient( 97.13% 60.1% at 0% 0%, rgba(0,188,0,0.35) 0%, #121923 100%), rgb(17, 25, 35)',
          },
          icon: require('@/assets/treasurebox/5.png'),
          get title() {
            return lang.v.tb_xian_fan;
          },
          get text() {
            return '0.00';
          },
          tipsIcon: require('@/assets/treasurebox/666.png'),
          get btn() {
            return {
              btnText: cashback.isActive
                ? lang.v.tb_xian_jiang
                : cashback.startTime,
              active: cashback.isActive,
            };
          },
        },
        {
          key: 1.4,
          background: {
            background:
              'radial-gradient( 97.13% 60.1% at 0% 0%, rgba(241,63,66,0.35) 0%, #121923 100%), rgb(17, 25, 35)',
          },
          icon: require('@/assets/treasurebox/3.png'),
          get title() {
            return lang.v.tb_red_desc;
          },
          get text() {
            return lang.v.tb_red_free;
          },
          get btn() {
            return {
              btnText: redEnvelopeRain.type.isStart
                ? lang.v.tb_red_ling
                : `${lang.v.tb_red_start}  ${redEnvelopeRain.type.time}`,
              active: redEnvelopeRain.type.isStart,
            };
          },
        },
        {
          key: 1.5,
          background: {
            background:
              'radial-gradient( 97.13% 60.1% at 0% 0%, rgba(254,205,71,0.35) 0%, #121923 100%), rgb(17, 25, 35)',
          },
          icon: require('@/assets/treasurebox/4.png'),
          get title() {
            return lang.v.tb_tui;
          },
          get text() {
            return lang.v.tb_tui_yao;
          },
          get tips() {
            return lang.v.tb_tui_tips;
          },
          tipsIcon: require('@/assets/treasurebox/666.png'),
          btn: {
            btnText: `${lang.v.tb_tui_yong} 0.00`,
            active: true,
          },
        },
        {
          key: 1.6,
          background: {
            background:
              'radial-gradient( 97.13% 60.1% at 0% 0%, rgba(237,103,173,0.35) 0%, #121923 100%), rgb(17, 25, 35)',
          },
          icon: require('@/assets/treasurebox/6.png'),
          get title() {
            return lang.v.tb_compet_title;
          },
          get text() {
            return lang.v.tb_compet_text;
          },
          get tips() {
            return lang.v.tb_compet_tips;
          },
          tipsIcon: require('@/assets/treasurebox/666.png'),
          btn: {
            get btnText() {
              return lang.v.tb_compet_fa;
            },
            active: true,
          },
        },
      ],
    },
    {
      key: 2,
      get title() {
        return lang.v.tb_te_jinag;
      },
      content: [
        {
          key: 2.1,
          background: {
            background:
              'linear-gradient(149deg, rgba(50,121,251,0.31) 0%, rgba(18,25,35,0) 100%)',
          },
          icon: require('@/assets/treasurebox/1-1.png'),
          get title() {
            return lang.v.tb_te_telegram;
          },
          get text() {
            return lang.v.tb_te_huo;
          },
          get amount() {
            return lang.v.tb_te_amount;
          },
          get text2() {
            return lang.v.tb_te_jiang;
          },
        },
      ],
    },
    {
      key: 3,
      get title() {
        return lang.v.tb_qi_jiang;
      },
      content: [
        {
          key: 3.1,
          background: {
            background:
              'linear-gradient( 149deg, rgba(247,184,80,0.3) 0%, rgba(18,25,35,0) 100%)',
          },
          icon: require('@/assets/treasurebox/2-1.png'),
          get title() {
            return lang.v.tb_qi_huo;
          },
          get text() {
            return lang.v.tb_qi_send;
          },
        },
        {
          key: 3.2,
          background: {
            background:
              'linear-gradient( 149deg, rgba(237,6,80,0.31) 0%, rgba(18,25,35,0) 100%)',
          },
          icon: require('@/assets/treasurebox/3-1.png'),
          get title() {
            return lang.v.tb_qi_hui;
          },
          get text() {
            return lang.v.tb_qi_log;
          },
        },
      ],
    },
  ] as TreasureBoxRewardCategory[]);
  public get activeCount(): number {
    return this.treasureBoxReward[0].content.filter((item) => item.btn?.active)
      .length;
  }
  openTreasureBox() {
    if (dataCenter.layout.isMobile) {
      this.openDownMenu = true;
      return;
    }
    this.showMenu = true;
  }
  closeTreasureBox() {
    if (dataCenter.layout.isMobile) {
      this.openDownMenu = false;
      return;
    }
    this.showMenu = false;
  }
  openActive(item?: TreasureBoxRewardItem) {
    if (!item || (!item.btn?.active && item.key < 2)) return;
    this.closeTreasureBox();
    const key = item.key;
    if (key != 1.6) {
      dataCenter.layout.closeAll();
    }
    switch (key) {
      case 1.1:
        openModal(WheelPop);
        break;
      case 1.2:
        openModal(Receba);
        break;
      case 1.3:
        getRouter().push('/cashback');
        break;
      case 1.4:
        redEnvelopeRain.open();
        break;
      case 1.5:
        getRouter().push('/referral/dashboard');
        break;
      case 1.6:
        dataCenter.layout.closeRight(RightSideBarState.COMPETITION);
        dataCenter.battle = 0;
        dataCenter.layout.openRight(RightSideBarState.COMPETITION);
        break;
      case 2.1:
        window.open(dataCenter.webConfig.channelLink, '_blank');
        break;
      case 3.1:
        break;
      case 3.2:
        openModal(reentryRewardVue, { hideClose: true });
        break;
      default:
        break;
    }
  }
}
export const bonusCabinet = reactive(new BonusCabinet());
