import { stringify } from 'qs';
import { GameData } from '@/api/protocol';
import { greenToBrazil } from '@/utils';

export async function login(email: string, password: string, client?: string) {
  try {
    const resp = await post<LoginResp, LoginReq>(LOGIN_URL, {
      email,
      password,
      client,
    });
    return resp;
  } catch (error) {
    return undefined;
  }
}

export async function regiter(
  type: number,
  email: string,
  password: string,
  avatar: string,
  country: string,
  code?: string,
  referee?: string,
  pixelId?: string,
  channel?: string,
) {
  try {
    const resp = await post<string, RegiterReq>(REGITER_URL, {
      type,
      email,
      password,
      avatar,
      country,
      code,
      referee,
      pixelId,
      channel,
    });

    return resp;
  } catch (error) {
    return undefined;
  }
}

export async function getUser() {
  try {
    const resp = await get<GetUserResp>(GETUSER_URL, {});

    return resp;
  } catch (error) {
    return undefined;
  }
}

//充值前获取货币
export async function getCurrencyAndNetwork() {
  try {
    const resp = await get<getCurrencyAndNetworkResp>(
      GET_CURRENCY_ANDNETWORK,
      {},
    );

    return resp;
  } catch (error) {
    return undefined;
  }
}
export let gameCategoryCache: getGameGroupResp[];
//获取游戏分组
export async function getGameCategory() {
  if (gameCategoryCache) return gameCategoryCache;
  try {
    gameCategoryCache = await get<getGameGroupResp[]>(GET_GAME_GROUP, {});

    return gameCategoryCache;
  } catch (error) {
    return undefined;
  }
}

//游戏记录
export async function getAllGameRecord(
  gameId?: number,
  limit?: number,
  historyId?: number,
  order?: string,
) {
  try {
    const resp = await post<GetAllGameRecordResp[], GetAllGameRecordReq>(
      GET_ALL_GAME_RECORD,
      {
        gameId,
        limit,
        historyId,
        order,
      },
    );
    resp.forEach((item) => {
      item.createTime = greenToBrazil(item.createTime);
    });
    return resp;
  } catch (error) {
    return undefined;
  }
}

